import React, {useState} from 'react';
import "./App.css"
import Nota from "./componentes/nota"
import Resultado from "./componentes/resultado"


const App = () => {
     const [valor, SetValor]=useState({"nota1":"","nota2":"","nota3":"","nota4":""})

return (
    <main class="App">
        <Nota num={1} valor={valor} setValor={setValor} /><br />
        <Nota num={2} valor={valor}setValor={setValor}/><br />
        <Nota num={3} valor={valor}setValor={setValor}/><br />
        <Nota num={4} valor={valor}setValor={setValor}/><br />

        <Resultado SomaNotas={parseFloat(nota1)+parseFloat(nota2)+parseFloat(nota3)+parseFloat(nota4)}/>
     </main>)
}

export default App;
